import React, { useState } from "react";

import MoviesList from "./components/MoviesList";
import "./App.css";

function App() {
  const [movies, setMovies] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);

  // const fetchMoviesHandler = new Promise((resolveFun, rejectFun) => {
  //   let connect = false;
  //   if (connect) {
  //     resolveFun("connection is established!");
  //   } else {
  //     rejectFun("connection is not established!");
  //   }
  // });
  // fetchMoviesHandler.then(
  //   (resultValue) => console.log(`Good: ${resultValue}`),
  //   (rejectValue) => console.log(`Bad: ${rejectValue}`)
  // );
  // console.log(fetchMoviesHandler);

  async function fetchMoviesHandler() {
    try {
      setError(null);
      setIsLoading(true);
      const response = await fetch("https://swapi.py4e.com/api/film");
      const data = await response.json();
      const transformedData = data.results.map((movieData) => {
        // results is a property for the data
        return {
          id: movieData["episode_id"],
          title: movieData.title,
          openingText: movieData["opening_crawl"],
          releaseDate: movieData["release_date"],
        };
      });
      setIsLoading(false);
      setMovies(transformedData);
    } catch (error) {}
  }

  return (
    <React.Fragment>
      <section>
        <button onClick={fetchMoviesHandler}>Fetch Movies</button>
      </section>

      <section>
        {!isLoading && movies.length === 0 && (
          <p>No movies yet! Pleas click on fetch!</p>
        )}
        {isLoading && <p style={{ color: "red" }}>Loading...</p>}
        {!isLoading && <MoviesList movies={movies} />}
      </section>
    </React.Fragment>
  );
}

export default App;
