import React, { useState, useEffect, useCallback } from "react";

import MoviesList from "./components/MoviesList";
import "./App.css";

function App() {
  const [movies, setMovies] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetchMoviesHandler();
  }, []);

  // const fetchMoviesHandler = new Promise((resolveFun, rejectFun) => {
  //   let connect = false;
  //   if (connect) {
  //     resolveFun("connection is established!");
  //   } else {
  //     rejectFun("connection is not established!");
  //   }
  // });
  // fetchMoviesHandler.then(
  //   (resultValue) => console.log(`Good: ${resultValue}`),
  //   (rejectValue) => console.log(`Bad: ${rejectValue}`)
  // );
  // console.log(fetchMoviesHandler);

  const fetchMoviesHandler = useCallback(async () => {
    try {
      setError(null);
      setIsLoading(true);
      const response = await fetch("https://swapi.py4e.com/api/films");
      console.log(response);
      if (!response.ok) {
        throw new Error("Something went wrong!");
      }

      const data = await response.json();
      const transformedData = data.results.map((movieData) => {
        // results is a property for the data
        return {
          id: movieData["episode_id"],
          title: movieData.title,
          openingText: movieData["opening_crawl"],
          releaseDate: movieData["release_date"],
        };
      });
      setMovies(transformedData);
    } catch (error) {
      setError(error.message);
    }
    setIsLoading(false);
  });

  // let content;
  // switch (content) {
  //   case movies.length === 0:
  //     content = <MoviesList movies={movies} />;
  //     break;
  //   case error:
  //     content = <p>{error}</p>;
  //     break;
  //   case isLoading:
  //     content = <p style={{ color: "red" }}>Loading...</p>;
  //     break;

  //   default:
  //     content = <p>No movies yet!</p>;
  //     break;
  // }

  return (
    <React.Fragment>
      <section>
        <button onClick={fetchMoviesHandler}>Fetch Movies</button>
      </section>

      <section>
        {!isLoading && error && <p>{error}</p>}
        {!isLoading && movies.length === 0 && !error && <p>No movies yet!</p>}
        {isLoading && <p style={{ color: "red" }}>Loading...</p>}
        {!isLoading && <MoviesList movies={movies} />}
        {/* {content} */}
      </section>
    </React.Fragment>
  );
}

export default App;
